import './App.css';
import Filtered from './filter.components';
import CharactersList from './allChars.component';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import AllCharactersList from './allChars.component';
import {
  ApolloClient,
  InMemoryCache,
  ApolloProvider,
  useQuery,
  gql
} from "@apollo/client";

const client = new ApolloClient({
  uri: 'https://rickandmortyapi.com/graphql',
  cache: new InMemoryCache(),
})
function App() {


  return (
    <>
      <ApolloProvider client={client}>

        <Filtered/>
        <div className="main-column">
          <AllCharactersList />
        </div>
      </ApolloProvider>
    </>
  );
}

export default App;
