import React, { useState } from 'react';
import './App.css';
import AllCharactersList from './allChars.component';
import {
  ApolloClient,
  InMemoryCache,
  ApolloProvider,
  useQuery,
  gql
} from "@apollo/client";

const client = new ApolloClient({
  uri: 'https://rickandmortyapi.com/graphql',
  cache: new InMemoryCache(),
})
function App() {
  const [filteredName, setFilteredName] = useState("");

  const filteredValueDetecter = (
    e: React.ChangeEvent<HTMLInputElement>
  ) => {
    setFilteredName(e.currentTarget.value);
  };


  return (
    <>
      <div>

          <input type="text"
      value={filteredName}
      onChange={filteredValueDetecter}></input>


      </div>
      <ApolloProvider client={client}>
        <div className="main-column">
          <AllCharactersList na={filteredName}/>
        </div>
      </ApolloProvider>
    </>
  );
}

export default App;
