import React from "react";
import { useQuery } from "@apollo/client";
import { CHARACTERS_QUERY } from "./useCharQuery";
import "./characterList.css"
import CharacterList from "./charList.component"


type Character = {
    id: number,
    name: string,
    image: string,
    location: Location,
};
type Location = {
    name: string,
}
type Characters = {
    results: Character[]
    info: {
      next: number
    }
  };
const CharactersList = () => {
    const { loading, error, data , fetchMore} = useQuery(CHARACTERS_QUERY);
    if (loading) return <p>Loading...</p>;
    if (error) return <p>Error</p>;
    console.log(data);
    return data.characters.results.map((items: Character, index: number) => (
        <div key={items.id} className="card-content">

            <CharacterList
                id={items.id}
                name={items.name}
                location={items.location}
                image={items.image}
            />
        </div>
    ));
};

export default CharactersList;
