import React from "react";
import { useQuery } from "@apollo/client";
import { CHARACTERS_QUERY } from "./useCharQuery";
import "./characterList.css"
import CharacterList from "./charList.component"

type Character = {
    id: number,
    name: string,
    image: string,
    location: Location,
};
type Location = {
    name: string,
}
const CharactersList = () => {
    const { loading, error, data } = useQuery(CHARACTERS_QUERY);
    if (loading) return <p>Loading...</p>;
    if (error) return <p>Error :</p>;
    console.log(data);
    return data.characters.results.map((character: Character, index: number) => (

        <div key={character.id} className="card-content">

            <CharacterList
                id={character.id}
                name={character.name}
                location={character.location}
                image={character.image}
            />
        </div>
    ));
};

export default CharactersList;
