import React from "react";
import { useQuery } from "@apollo/client";
import { CHARACTERS_QUERY } from "./useCharQuery";
import "./characterList.css"
import CharList from "./charList.component"

interface AllResponseVars {
    page: number;
    name?: String;
}
export interface CharacterData {
    characters: Characters
}
type Character = {
    id: number,
    name: string,
    image: string,
    location: Location,
};
type Location = {
    name: string,
}
type Characters = {
    results: Character[]
    info: {
        next: number
    }
};
const CharactersList:React.FC = () => () => {
    const { loading,data, error, fetchMore } = useQuery<CharacterData, AllResponseVars>(CHARACTERS_QUERY);
    if (loading) return <p>Loading...</p>;
    if (error) return <p>Error</p>;
    console.log(data);
    var characters = data.characters.results;
    return data.characters.results.map((items: Character, index: number) => (
        <div key={items.id} className="card-content">

            <CharList
                id={items.id}
                name={items.name}
                location={items.location}
                image={items.image}
            />
        </div>
    ));
};

export default CharactersList;
