import React from "react";
import { useQuery } from "@apollo/client";
import { CHARACTERS_QUERY } from "./useCharQuery";
import "./characterList.css"

type Character = {
    id: number,
    name: string,
    image: string,
    location: Location,
};
type Location = {
    name: string,
}
const CharactersList = () => {
    const { loading, error, data } = useQuery(CHARACTERS_QUERY);
    if (loading) return <p>Loading...</p>;
    if (error) return <p>Error :(</p>;
    console.log(data);
    return data.characters.results.map((character: Character) => (
        <div key={character.id} className="dev-class">
            <img src={character.image} alt="img" />
            <div className="alt-kisim">
                <p><strong>id:</strong> {character.id}</p>
                <p>
                    <strong>Character name:</strong> {character.name}
                </p>
                <p><strong>Location:</strong> {character.location.name}</p>
            </div>
        </div>
    ));
};

export default CharactersList;
