import React from 'react';
import './App.css';
import CharactersList from './all.chars.component';

import {
  ApolloClient,
  InMemoryCache,
  ApolloProvider,
  useQuery,
  gql
} from "@apollo/client";

const client = new ApolloClient({
  uri: 'https://rickandmortyapi.com/graphql',
  cache: new InMemoryCache(),
})
function App() {
  return (
    <>
      <ApolloProvider client={client}>

        <div className="main-column">
          <CharactersList />
        </div>
      </ApolloProvider>
    </>
  );
}

export default App;
